var now = require('performance-now');

var start = now();

var largeNum = "";


function sumOfDigits(n) {
    var num = n;
    var sum = 0;
    for (var i = 0; i < num.length; i++) {
        sum += parseInt(num.charAt(i));
    }
    return sum;
};

console.log(sumOfDigits(largeNum));

var end = now();

console.log("Run time: " + (end - start));